double Average(double a[][M], int n) {
    double sum = 0;

    // è®¡ç®—æ¯è¡Œçš„æœ€å¤§å€¼å¹¶ç´¯åŠ åˆ°æ€»å’Œ
    for (int i = 0; i < n; i++) {
        double max = a[i][0];
        for (int j = 1; j < M; j++) {
            if (a[i][j] > max) {
                max = a[i][j];
            }
        }
        sum += max;
    }

    // è®¡ç®—æ¯åˆ—çš„æœ€å°å€¼å¹¶ç´¯åŠ åˆ°æ€»å’Œ
    for (int j = 0; j < M; j++) {
        double min = a[0][j];
        for (int i = 1; i < n; i++) {
            if (a[i][j] < min) {
                min = a[i][j];
            }
        }
        sum += min;
    }

    // è¿”å›å¹³å‡å€¼
    return sum / (n + M);
}
